mapper.select:
SELECT COUNT(*)
FROM TEACHER
WHERE TC_ID=#{id}

SELECT COUNT(*)
FROM STUDENT
WHERE ST_ID=#{id}

SELECT COUNT(*)
FROM STUDENT
WHERE ST_RFSTUDENTCODE=#{studentCode}

mapper.update:
UPDATE TEACHER
SET
TC_PWD=#{pwd}
WHERE
TC_ID=#{id}

UPDATE STUDENT
SET
ST_PWD=#{pwd}
WHERE
ST_RFSTUDENTCODE=#{studentCode}

UPDATE
WRONGANSWERNOTESTAG
SET
WTAG_CONTENT=#{boardContent}
WHERE
WTAG_WANLRCODE=#{roomCode} AND WTAG_WANCODE=#{boardCode}

mapper.delete:
DELETE
REFERENCE
WHERE RR_LRCODE=#{roomCode}
AND RR_TITLE = #{boardTitle}
AND RR_TCID =
#{boardId}
AND RR_DATE = TO_DATE(#{boardDate},'YYYY-MM-DD
HH24:MI:SS')

DELETE FROM
QUERY WHERE
QU_LRCODE = #{roomCode}
AND QU_DATE =
TO_DATE(#{boardDate},'YYYY-MM-DD
HH24:MI:SS')
AND
QU_STRFSTUDENTCODE = #{boardId}
AND QU_TITLE =
#{boardTitle}

