mapper.delete:
DELETE
FROM TASK
WHERE
TA_LRCODE = #{roomCode}
AND
TA_CODE = #{boardCode}

mapper.update:
UPDATE TASK
SET
TA_TITLE=#{boardTitle},TA_CONTENT=#{boardContent}
WHERE
TA_LRCODE =
#{roomCode} AND TA_CODE = #{boardCode}

mapper.select:
SELECT
TS_ROUTE AS boardRoute,
TS_DATET AS boardDate
FROM TASKSUBMIT
WHERE
TS_TALRCODE= #{roomCode}
AND
TS_TACODE = #{boardCode}
AND
TS_STRFSTUDENTCODE = #{studentCode}

mapper.delete:
DELETE MESSAGE 
WHERE
MS_OTHER =#{studentCode}
OR
MS_ID =#{studentCode}

mapper.select:
SELECT
TS_ROUTE AS boardRoute,
TS_DATET AS boardDate
FROM TASKSUBMIT
WHERE
TS_TALRCODE= #{roomCode}
AND
TS_TACODE = #{boardCode}
AND
TS_STRFSTUDENTCODE = #{studentCode}

select SLOH_DATE AS attendDate,SLOH_TYPE AS attendanceType 
from STUDENTLOGOUTHISTORY 
where SLOH_STRFSTUDENTCODE=#{studentCode}
ORDER BY sloh_date desc;
