mapper.select:
SELECT COUNT(*)
FROM TEACHER
WHERE TC_ID=#{id}

SELECT COUNT(*)
FROM STUDENT
WHERE ST_ID=#{id}

SELECT COUNT(*)
FROM STUDENT
WHERE ST_RFSTUDENTCODE=#{studentCode}

mapper.update:
UPDATE TEACHER
SET
TC_PWD=#{pwd}
WHERE
TC_ID=#{id}

UPDATE STUDENT
SET
ST_PWD=#{pwd}
WHERE
ST_RFSTUDENTCODE=#{studentCode}
